import urllib2
import json

"""
Python script to get all the descriptions of each disease, based on the id of each disease.
Crawls the HealthService API we're currently using, stores it in local json file
"""

nums = [131, 324, 109, 166, 51, 79, 357, 50, 489, 347, 167, 446, 18, 376, 68, 67, 103, 19, 510, 476, 488, 151, 497, 59]

finalData = ""

for i in nums:

    url = "https://sandbox-healthservice.priaid.ch/issues/" + str(i) + "/info?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..VU8cM-jQdJSjCqALv6TdnXdthc4CQDoXePCVUDf7sBw&language=en-gb&"

    req = urllib2.Request(url)
    data = urllib2.urlopen(req).read()
    respjson = json.loads(data.decode("utf-8"))

    finalData += "\"" + str(i) + "\":" + json.dumps(respjson) + ","

print(finalData)
